import {Dictionary, Try, windoww} from '../joiner';
import {Dashboard} from './components';

import {Cards, Card} from './components/cards/Cards';
import { Catalog } from './components/catalog/Catalog';
import {ReactNode, useState} from "react";

type keys = 'fix' | 'newbug' | 'opt' | 'feat' | 'info';

class Version{
    static all: Version[] = [];
    entries: Dictionary<keys, ReactNode[]>;
    constructor(number: string, name:string, data: string){
        this.entries = {fix:[], newbug:[], opt:[], feat:[], info:[]};
        Version.all.push(this);
    }
    add(key: keys, node: ReactNode): this { this.entries[key].push(node); return this; }
    fix(node: ReactNode): this { this.add('fix', node); return this; }
    newbug(node: ReactNode): this { this.add('newbug', node); return this; }
    opt(node: ReactNode): this { this.add('opt', node); return this; }
    feat(node: ReactNode): this { this.add('feat', node); return this; }
    info(node: ReactNode): this { this.add('info', node); return this; }
}

function UpdatesPage(): JSX.Element {
    // NB: this works only in production if you put subfolders with past builds in the new build root.
    let [info, setInfo] = useState(true);
    let [feat, setFeat] = useState(true);
    let [fix, setFix] = useState(true);
    let [newbug, setNewbug] = useState(true);
    let [opt, setOpt] = useState(false);
    let cards: ReactNode =
        <Cards>
            <Cards.Item
                title={'Getting started'}
                subtitle={'Create your first notation.'}
                icon={'gettingstarted'}
                style={'rainbow'}
            />
            {true && <Cards.Item icon={'question'} style={'clear'} title={'Ehy!'} subtitle={'What do you want to do today?'}/>}
        </Cards>;
    cards = null;

    return(<Try>
        <Dashboard active={'Updates'} version={{n: 0, date:'fake-date'}}>
            <div id={'updates-page'}>{cards}
                <h2>Past versions
                    <select className={'ms-2'} onChange={(e) => windoww.location = (window.location.origin + e.target.value) as any}>
                        <option value='/jjodel/2.2' title={'13/nov/2024'}>2.2</option>
                        <option value='/jjodel/2.1' disabled>2.1</option>
                        <option value='/jjodel/2.0' disabled>2.0</option>
                    </select>
                </h2>
                <div className={'filter-container'}>
                    <button className={'feat btn-'+(feat?'':'outline-')+'info'} onClick={e=>setFix(!feat)}>Feature</button>
                    <button className={'fix btn-'+(newbug?'':'outline-')+'success'} onClick={e=>setFix(!fix)}>Bugfix</button>
                    <button className={'newbug btn-'+(newbug?'':'outline-')+'danger'} onClick={e=>setNewbug(!newbug)}>New known bug</button>
                    <button className={'info btn-'+(info?'':'outline-')+'secondary'} onClick={e=>setInfo(!info)}>Information</button>
                    <button className={'opt btn-'+(opt?'':'outline-')+'warning'} onClick={e=>setInfo(!opt)}>Optimization</button>
                </div>
                <ul className={'version-list'}>
                    <li className={'version'}>
                        <h2 className={'version-name'}>2.2 manatee (11/nov/2024)</h2>
                        <ul className={'entry-list'}>
                            <li className={'entry'}></li>

                        </ul>
                    </li>

                </ul>
            </div>
        </Dashboard>
    </Try>)
        ;
}

export {UpdatesPage};
