import React, {MouseEventHandler, ClassAttributes, useState, useRef, useEffect, ReactElement, ReactNode} from "react";
import './commandbar.scss';
import { inherits } from "util";
import { Tooltip } from "../forEndUser/Tooltip";
import { U } from "../../joiner";

type BtnProps = {
    disabled?: boolean;
    active?: boolean;
    icon: "up"
        | "down"
        | "back"
        | "fwd"
        | "add"
        | "add2"
        | "delete"
        | "delete2"
        | "open"
        | "edit"
        | "favorite"
        | "shrink"
        | "expand"
        | "space"
        | "minispace"
        | "sep"
        | "check"
        | "copy"
        | "close"
        | "info"
        | "show"
        | "open-down"
        | "close-up"
        | "settings"
        | "download";

    tip?: string | ReactNode;
    label?: string;
    theme?: "dark" | "light",
    action?: MouseEventHandler,
    size?: "x-small" | "small" | "medium" | "large",
    style?: React.CSSProperties,
    mode?: 'normal' | 'negative'
    classNam?: string;
    needConfirm?:boolean;
}


export const Btn = (props: BtnProps) => {
    const [confirm, setConfirm] = useState(props.icon === 'delete' && !props.disabled || props.needConfirm);
    const mode = (props.mode ? props.mode : 'normal');
    let i_classes = (props.className||'') + ` bi tab-btn commandbar-btn ${confirm ? '' : props.icon} ${props.theme ? props.theme : 'light'} ${props.size||''} ${mode} ${props.disabled ? 'disabled ' : ''}`
    let action = (e: React.MouseEvent<any,any>) => {
        if (props.disabled || !props.action) return;
        if (confirm) { setConfirm(false); return; }
        props.action(e);
        e.stopropagation();
        U.clickedOutside(e.currentTarget, ()=>setConfirm(true))
    }
    let icon: ReactNode = null;
    if (icon === 'delete2') icon = <div className={`delete2 ${props.theme ? props.theme : 'light'}`}>Delete</div>


    if (icon === 'delete') icon = <Tooltip tooltip={!confirm ? 'Are you sure?' : props.tip} inline={true} position={'top'} offsetY={10} >
        <i onClick={action} style={props.style} className={i_classes + (!confirm ? `bi-question-square-fill question` : '')} />
    </Tooltip>

    return (<>
        {props.action ?
            <div className={'btn-component '+(props.active ? 'active' : '')}>{icon}</div>
        :
            <>
                {props.icon === "space" || props.icon === "minispace" ?
                    <span style={{display: 'block', width: `${props.icon === 'space' ? '24px' : '4px'}`}}></span>
                :
                <Tooltip tooltip={'Disabled'} inline={true} position={'top'} offsetY={10} >
                    <i className={i_classes + ``} style={props.style} />
            </Tooltip>
                }
            </>
        }
    </>);
}



export const Sep = (style?: any) => {

    return (<>
            {style ?
                <div>
                    <div className={'tab-btn sep'}></div>
                </div>
            :
                <div>
                    <div className={'tab-btn sep'} style={style}></div>
                </div>
            }

    </>);
}


type CommandProps = {
    children: any,
    style?: React.CSSProperties,
    className?: string,
    noBorder?: boolean;
}

export const CommandBar = (props: CommandProps) => {

    let style = props.style;

    let noBorder = (props.noBorder ? props.noBorder: false);

    return(<>
        {props.style ?
            <div className={`command-bar ${props.className && props.className} ${noBorder && 'no-border'}`} style={props.style}>
                {props.children}
            </div>
            :
            <div className={`command-bar ${props.className && props.className} ${noBorder && 'no-border'}`} >
                {props.children}
            </div>
        }

    </>);
};
