import React, {Dispatch, isValidElement, KeyboardEvent, ReactElement, ReactNode} from 'react';
import {connect} from 'react-redux';
import {DState} from '../../redux/store';
import {
    Defaults,
    DPointerTargetable,
    DV,
    GObject,
    Keystrokes,
    Log,
    LPointerTargetable,
    Overlap,
    Pointer, store,
    U, LoggerCategoryState, RuntimeAccessible
} from '../../joiner';
import './tooltip.scss';
class TooltipVisualizerState{
    tooltip?: ReactNode;
}
export class TooltipVisualizer extends React.Component<{}, TooltipVisualizerState> {
    public static component: TooltipVisualizer;
    constructor(){
        super({});
        this.state = new TooltipVisualizerState();
        TooltipVisualizer.component = this;
    }
    render(){
        return this.state.tooltip || undefined;
    }
}

@RuntimeAccessible('Tooltip')
export class Tooltip extends React.Component<AllProps, State> {
    static cname: string = "Tooltip";

    constructor(props: AllProps) {
        super(props);
        this.state = { };
        this.onMouseEnter = this.onMouseEnter.bind(this);
        this.onMouseLeave = this.onMouseLeave.bind(this);
    }
    public static show(tooltip: ReactNode): void{
        TooltipVisualizer.component.setState({tooltip});
    }
    public static hide(): void {
        TooltipVisualizer.component.setState({tooltip: undefined});
    }

    onMouseEnter(e?: MouseEvent): void{
        Tooltip.show(this.props.tooltip);
    }
    onMouseLeave(e?: MouseEvent): void{
        Tooltip.show(this.props.tooltip);
    }

    componentWillUnmount() {
        this.onMouseLeave();
    }

    render() {
        if (!this.props.tooltip) return this.props.children;
        if (Array.isArray(this.props.children)) return <span>
            &lt;Tooltip /&gt; component requires exactly 1 element as children. Wrap the subelements in a container element.
        </span>
        if (!this.props.children || !isValidElement(this.props.children)) return <span>
            &lt;Tooltip /&gt; component requires a html or react node as children.
        </span>
        for (let k of Object.keys(this.props)) switch (k){
            default: Log.ww("<Tooltip /> component cannot accept props other than \"key\"."); break;
            case 'key': case 'children': case 'tooltip': break;
        }

        let c = this.props.children;
        let onMouseEnter = c.props.onMouseEnter ? (...a:any[])=>{
            let ret: any = undefined;
            if (typeof c.props.onMouseEnter !== "function") {
                Log.ww("<Tooltip /> component requires his child to have onMouseEnter props either missing or a valid function");
            } else ret = c.props.onMouseEnter(...a);
            //@ts-ignore spread
            this.onMouseEnter(...a);
            return ret;
        } : this.onMouseEnter;
        let onMouseLeave = c.props.onMouseLeave ? (...a:any[])=>{
            let ret: any = undefined;
            if (typeof c.props.onMouseLeave !== "function") {
                Log.ww("<Tooltip /> component requires his child to have onMouseLeave props either missing or a valid function");
            } else ret = c.props.onMouseLeave(...a);
            //@ts-ignore spread
            this.onMouseLeave(...a);
            return ret;
        } : this.onMouseLeave;

        let ret = React.cloneElement(c, {onMouseEnter, onMouseLeave});
        return ret;
    }

}
interface State{
}
interface OwnProps {
    key?: React.Key | null;
    catch?: ReactNode | ((error: Error, info?: React.ErrorInfo) => ReactNode);
    children: ReactNode;
    tooltip: ReactNode;
}
interface StateProps {
}
interface DispatchProps { }
type AllProps = Overlap<OwnProps, Overlap<StateProps, DispatchProps>>;


