import { reduce } from "../reduce";
export function groupBy(children, fn, options = {}) {
    return reduce(children, (acc, current, idx) => {
        const key = fn(current, idx);
        const res = acc[key];
        if (res) {
            res.push(current);
        }
        else {
            acc[key] = [current];
        }
        return acc;
    }, {}, options);
}
